import React, {useState,useEffect} from 'react';
import axios from "axios";

import '../App.css';

///TESTING API FUNCTIONALITY

const apiKey='AIzaSyDQgG9PY_tH65Mss-EP1a8M_YQNZqORmys'

//search in title and author
let booksUrl = 'https://www.googleapis.com/books/v1/volumes?q=atomic+inauthor:clear&printType=books&projection=lite&key=AIzaSyDQgG9PY_tH65Mss-EP1a8M_YQNZqORmys'

//get singular result
// uses volume ID 
let single = "https://www.googleapis.com/books/v1/volumes/XfFvDwAAQBAJ?key=AIzaSyDQgG9PY_tH65Mss-EP1a8M_YQNZqORmys"

function App() {
  let [books,setBooks]= useState([])

  // useEffect(()=>{
  //   fetch(booksUrl)
  //   .then(r=>r.json())
  //   .then(bookItems=>console.log(bookItems))
  // }, [])

  const [book,setBook]=useState('');

  function handleChange(event){
    const search = event.target.value;
    setBook(search);
  }

  function handleSubmit(event){
    event.preventDefault();
    axios.get(`https://www.googleapis.com/books/v1/volumes?q=${book}&printType=books&key=${apiKey}&maxResults=40`)
    .then(r=>
      setBooks(r.data.items))
  }

  let booksToDisplay = books.map((book)=>{
    <img src={book.volumeInfo.imageLinks.thumnail} alt={book.title}/>
  })

  return (
    <div className="App">
      <div className='container'>
        <h1>Book-Report</h1>
        <form onSubmit={handleSubmit}>
            <div className='form-group'>
                <input 
                  type='text' onChange={handleChange}
                  className='form-control mt-10' 
                  placeholder='Search for Books' 
                  autoComplete='off' />
            </div>
            <button 
              type='submit' 
              className='btn btn-danger'  
            >Search</button>
        </form>
        {booksToDisplay}
      </div>
    </div>
  );
}

export default App;
